<script>
    $('#follow').on('click', function (){
        var label = $('#follow').next();
        $follow = $(this).is(':checked');
        $.ajax({
            type: 'GET',
            url: '{{ route('follow') }}',
            data: {
                'follow': $follow,
                'followed_user': '{{ $user->id }}',
                'follower_id': '{{Auth::user()->id}}'
            },
            success: function(data) {
                label.text(data)
            }
        });
    });
</script>

